# RUN_STEP: <elaborate>
elaborate()
{
  opts="-load "D:/Xilinx/Vivado/2015.4/lib/win64.o/libxil_vcs.dll:xilinx_register_systf" -full64 -debug_pp -t ps -licqueue -l elaborate.log"

  vcs $opts xil_defaultlib.system xil_defaultlib.glbl -o system_simv
}

# RUN_STEP: <simulate>
simulate()
{
  opts="-ucli -licqueue -l simulate.log"

  ./system_simv $opts -do simulate.do
}
